def set_tags():
	data = json.load(open('food_db.json'))

	veg_non_veg = {'non vegetarian', 'vegetarian'}

	final_dict = {}

	set_unique_ingredients = set()

	for i in data:
		dishId = i["dish_id"]
		dishName = i["dish_name"].lower()
		ingredients = i["ingredients"]

		t = []

		if dishName in tags:
			t.extend(tags[dishName])
			

		for j in dishName.split():
			if j in tags:
				if tags[j] not in t:
					t.extend(tags[j])
					

			if j in tags2:
				if tags2[j] not in t:
					t.extend(tags2[j])
					

		for j in tags:
			if j in dishName:
				if tags[j] not in t:
					t.extend(tags[j])
					

		for j in ingredients:
			for k in j.lower().split():
				if k in tags:
					t.extend(tags[k])
					

				if k in tags2:
					t.extend(tags2[k])
					

		for j in ingredients:
			for k in tags:
				if k in j:
					t.extend(tags[k])
					

		non_veg = 0
		t = set(t)
		if 'non vegetarian' in t:
			non_veg = 1
		t = t - veg_non_veg

		set_unique_ingredients = set_unique_ingredients.union(set(t))
		final_dict[dishId] = (sorted(set(t)), non_veg)

	# pickle.dump(final_dict, open('tagged_dishes.pickle'), 'wb')
	return final_dict, set_unique_ingredients


res, res2 = set_tags()

indexed_tags = ['bellpepper', 'carrot', 'cauliflower', 'chili_green', 'chili_red', 'coconut', 'cumin', 'egg', 'eggplant', 'ginger', 'green_beans', 'kidneybean', 'lemon', 'mushroom', 'okra', 'onion', 'paneer', 'peas', 'potato', 'tomato']

reverse_index = {}

for i in res:
	for k in indexed_tags:
		if k in res[i][0]:
			if k in reverse_index:
				reverse_index[k].append(i)

			else:
				reverse_index[k] = [i]

# write reverse index to pickle file

import pickle
pickle.dump(reverse_index, open("reverse_index.pickle", "wb" ))
